import APanelVertical from './components/APanelVertical';
import APanelHorizontal from './components/APanelHorizontal';
import APanel from './components/APanel';

import interact from 'interactjs';
import simplebar from 'simplebar-vue';
import 'simplebar/dist/simplebar.min.css';

export default {
    components: {
        APanelVertical,
        APanelHorizontal,
        APanel,
    },
    install(Vue, options) {
        Vue.prototype.$interact = interact;
        Vue.component('simplebar', simplebar);
        Vue.component('ap-vertical', APanelVertical);
        Vue.component('ap-horizontal', APanelHorizontal);
        Vue.component('ap-panel', APanel);
        Vue.prototype.$getElementSizePercent = function(element) {
            let parent = element.offsetParent || element;
            let width = ((element.offsetWidth / parent.offsetWidth) * 100.0).toFixed(2);
            let height = ((element.offsetHeight / parent.offsetHeight) * 100.0).toFixed(2);
            return {width, height};
        }

    }
}
